import React, { useState } from 'react';
import { styled } from "@mui/material";

const Input = styled('input')({
    border: 'none',
    borderBottom: '2px solid black',
    marginBottom: '25px',
    fontSize: '22px',
    '&:focus': {
        borderBottom: '2px solid black', // Keeps the bottom border intact
        boxShadow: 'none', // Removes any box-shadow that might be added on focus
    },
});

function PasswordInput({id, placeholder}) {
    const [showPassword, setShowPassword] = useState(false);

    const handleVisibilityToggle = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return (
        <div style={{ position: 'relative' }}>
            <Input
                id={id}
                style={{ width: '100%' }}
                type={showPassword ? 'text' : 'password'}
                name={placeholder}
                placeholder={placeholder}
                pattern="\w{8}"
                title="8 character password"
            />
            <i id={`visibilitybtn-${id}`} onClick={handleVisibilityToggle} style={{ cursor: 'pointer' }}>
                <span id="v1" className="material-symbols-outlined" style={{ marginLeft: '-25px' }}>
                    {showPassword ? 'visibility_off' : 'visibility'}
                </span>
            </i>
        </div>
    );
}

export default PasswordInput;
