import React, { useState } from 'react';
import { styled } from "@mui/material";

const Input = styled('input')({
    border: 'none',
    borderBottom: '2px solid black',
    marginBottom: '25px',
    fontSize: '22px',
});

function PasswordInput() {
  const [showPassword, setShowPassword] = useState(false);

  const handleVisibilityToggle = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  return (
    <div style={{ position: 'relative' }}>
      <Input
        id="pass1"
        style={{ width: '100%' }}
        type={showPassword ? 'text' : 'password'}
        name="Password"
        placeholder="Password"
        pattern="\w{8}"
        title="8 character password"
      />
      <i id="visibilitybtn1" onClick={handleVisibilityToggle} style={{ position: 'absolute', right: '10px', top: '50%', transform: 'translateY(-50%)', cursor: 'pointer' }}>
        <span id="v1" className="material-symbols-outlined">
          {showPassword ? 'visibility_off' : 'visibility'}
        </span>
      </i>
    </div>
  );
}

export default PasswordInput;
