import frame from '../static/images/Frame.png';
import image10 from '../static/images/image 10.png';
import { styled } from "@mui/material";
import { useState } from "react";

const Input = styled('input')({
    border: 'none',
    borderBottom: '2px solid black',
    marginBottom: '25px',
    fontSize: '22px',
});

const Button = styled('button')({
    marginTop:'20px',
    borderRadius: '5px',
    background:'black',
    color:'white',
    textAlign:'center',
    fontSize: "25px",
    height: "50px",
})

export default function LoginSignUp() {
    const [acc, toggleAcc] = useState('login');
    return (
        <div style={{ display: 'flex', justifyContent: "center" }}>
            <img src={image10} alt='10' />
            <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', position: 'relative'}}>
                <div style={{ position: 'absolute', top: '0', width: '100%', display: 'flex', alignItems: 'center'}}>
                    <img src={frame} alt='frame' style={{ width: '24px', height: '24px'}} />
                    <h4 style={{ marginLeft: '1%' }}>User Interface Enhancement for Access Database</h4>
                </div>
                <div>
                    <h1>Welcome back</h1>
                    <p style={{fontSize:'20px'}}>Welcome back! Please enter your details.</p>
                    <br />
                    <form style={{ display: 'flex', flexDirection: 'column' }}>
                        <Input type='email' name='Email' placeholder='Email' />
                        <Input type='password' name='Password' placeholder='Password' pattern="\w{8}" title="8 character password" />
                        <a href='#' style={{alignSelf:'end'}}>Forgot Password</a>
                        <Button>Log In</Button>
                    </form>
                </div>
            </div>

        </div>
    )
}
