import { styled } from "@mui/material";
import React from "react";

const Input = styled('input')({
    border: '2px solid',
    borderRadius: '5px',
    fontSize: '25px',
    width: '50px',
    height: '50px',
    textAlign: 'center',
    margin: '10px',
    '&::-webkit-outer-spin-button': {
        WebkitAppearance: 'none',
        margin: 0,
    },
    '&::-webkit-inner-spin-button': {
        WebkitAppearance: 'none',
        margin: 0,
    },
    '&[type="number"]': {
        MozAppearance: 'textfield',
    }
})

const ButtonA = styled('button')({
    border: 'none',
    color: '-webkit-link',
    background: 'none',
    fontSize: '15px',
    textDecoration: 'underline',
    cursor: 'pointer'
})

export default function OTP_UI({ email }) {
    const [result, setResult] = React.useState("");
    const onSubmit = async (event) => {
        event.preventDefault();
        setResult("Sending....");
        const formData = new FormData(event.target);

        formData.append("access_key", "1b2814ca-ba6f-4a72-be3a-c8653da306fe");

        const response = await fetch("https://api.web3forms.com/submit", {
            method: "POST",
            body: formData
        });

        const data = await response.json();

        if (data.success) {
            setResult("Form Submitted Successfully");
            event.target.reset();
        } else {
            console.log("Error", data);
            setResult(data.message);
        }
    };

    const checkOTP = () => {

    }

    const resendOTP = () => {
        
    }

    return (
        <>
            <p style={{ fontSize: '22px', fontFamily: 'Arial', textAlign: 'center' }}>
                We sent an OTP to your email { }
            </p>
            <form onSubmit={onSubmit}>
                <div>
                    <Input id='num1' type="number" min={0} max={9} required />
                    <Input id='num2' type="number" min={0} max={9} required />
                    <Input id='num3' type="number" min={0} max={9} required />
                    <Input id='num4' type="number" min={0} max={9} required />
                    <Input id='num5' type="number" min={0} max={9} required />
                    <Input id='num6' type="number" min={0} max={9} required />
                </div>
                <button
                    style={{
                        margin: '0',
                        marginTop: '20px',
                        padding: '15px',
                        borderRadius: '15px',
                        fontSize: '30px',
                        background: '#3333f3',
                        color: 'white',
                        width: '100%',
                        border: 'none',
                        cursor: 'pointer'
                    }} onClick={checkOTP}>
                    Verify </button>
            </form>
            <p style={{textAlign:'center'}}>Didn't recieve a code?
                <ButtonA onClick={resendOTP}> 
                    Resend.
                </ButtonA>
            </p>
        </>
    )
}
