import { styled } from "@mui/material";
import React, { useState } from "react";

const Input = styled('input')({
    border: '1px solid',
    borderRadius: '5px',
    fontSize: '25px',
    width: '50px',
    height: '50px',
    textAlign: 'center',
    margin: '10px',
    '&::-webkit-outer-spin-button': {
        WebkitAppearance: 'none',
        margin: 0,
    },
    '&::-webkit-inner-spin-button': {
        WebkitAppearance: 'none',
        margin: 0,
    },
    '&[type="number"]': {
        MozAppearance: 'textfield',
    }
})

const ButtonA = styled('button')({
    border: 'none',
    color: '-webkit-link',
    background: 'none',
    fontSize: '15px',
    textDecoration: 'underline',
    cursor: 'pointer'
})

export default function OTP_UI({ email }) {
    const [otp, setOTP] = useState(new Array(6).fill(""))

    const handleChange = (e, index) => {
        if (isNaN(e.target.value)) return false;
        setOTP([...otp.map((data, indx) => (indx === index ? e.target.value : data))])
        if (e.target.value && e.target.nextSibling) {
            e.target.nextSibling.focus()
        }
    }

    const sendOTP = () => {
        let otp = '';
        for (let i = 0; i < 6; i++) {
            otp += Math.floor(Math.random() * 10);
        }
        const templateParams = {
            to_email: email,
            otp_code: otp,
        };

        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams, 'YOUR_USER_ID')
            .then((response) => {
                console.log('SUCCESS!', response.status, response.text);
            }, (error) => {
                console.log('FAILED...', error);
            });
    }

    const checkOTP = () => {

    }

    const resendOTP = () => {
        sendOTP();
    }

    return (
        <>
            <p style={{ fontSize: '22px', fontFamily: 'Arial', textAlign: 'center' }}>
                Please enter the OTP sent you at {email}
            </p>
            <form>
                <div>
                    {otp.map((data, i) => {
                        return <Input type="number" min={0} max={9} required
                            value={data} onChange={(e) => handleChange(e, i)} />
                    })}
                </div>
                <button
                    style={{
                        margin: '0',
                        marginTop: '20px',
                        padding: '15px',
                        borderRadius: '15px',
                        fontSize: '30px',
                        background: '#3333f3',
                        color: 'white',
                        width: '100%',
                        border: 'none',
                        cursor: 'pointer'
                    }} onClick={checkOTP}>
                    Verify </button>
            </form>
            <p style={{ textAlign: 'center' }}>Didn't recieve a code?
                <ButtonA onClick={resendOTP}>
                    Resend.
                </ButtonA>
            </p>
        </>
    )
}
