    const resendOTP = () => {

    }

    return (
        <>
            <p style={{ fontSize: '22px', fontFamily: 'Arial', textAlign: 'center' }}>
                Please enter the OTP sent to your email at {email}
            </p>
            <form>
                <div>
                    <Input id='num1' type="number" min={0} max={9} required />
                    <Input id='num2' type="number" min={0} max={9} required />
                    <Input id='num3' type="number" min={0} max={9} required />
                    <Input id='num4' type="number" min={0} max={9} required />
                    <Input id='num5' type="number" min={0} max={9} required />
                    <Input id='num6' type="number" min={0} max={9} required />
                </div>
                <button
                    style={{
                        margin: '0',
                        marginTop: '20px',
                        padding: '15px',
                        borderRadius: '15px',
                        fontSize: '30px',
                        background: '#3333f3',
                        color: 'white',
                        width: '100%',
                        border: 'none',
                        cursor: 'pointer'
                    }} onClick={checkOTP}>
                    Verify </button>
            </form>
            <p style={{ textAlign: 'center' }}>Didn't recieve a code?
                <ButtonA onClick={resendOTP}>
                    Resend.
                </ButtonA>
            </p>
        </>
    )
}
